mapper.update:
update group 


          name=#{group.name}


          uid=#{group.uid}


          create_time=#{group.createTime}


where id=#{gid}

mapper.select:
select id from group where id in (
  select uid from user_group where  uid =#{uid}
) ;

select * from user where id
in(select uid from user_group where gid=#{gid})
and id!=uid;
